import {
  Image,
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  Linking,
} from 'react-native';
import React, {useLayoutEffect, useRef} from 'react';

import {Avatar, colors} from 'react-native-elements';

const Settings = ({navigation}) => {
  useLayoutEffect(() => {
    navigation.setOptions({
      title: 'Settings ',
      headerStyle: {backgroundColor: '#1f9'},
      headerTitleStyle: {color: '#050a9c'},
      headTintColor: 'black',
    });
  }, [navigation]);

  return (
    <View>
      <Image
        width={150}
        height={150}
        source={{
          uri: 'data:image/png;base64,',
        }}
        style={styles.imagev}
      />
      <TouchableOpacity>
        <Text style={styles.textV}> Choose image</Text>
      </TouchableOpacity>
    </View>
  );
};

export default Settings;

const styles = StyleSheet.create({
  userAvatar: {
    flex: 1,
    bottom: -50,
  },
  imagev: {
    width: 150,
    height: 150,
    borderRadius: 100,
    alignSelf: 'center',
  },
  textV: {
    alignSelf: 'center',
    color: 'blue',
  },
});
