import {View, KeyboardAvoidingView, Text, Platform, TextInput, Modal, Pressable, StyleSheet, Image, Button} from 'react-native';
import React, {useState} from 'react';
import Colors from '../utils/Colors.js';
import ButtonCustom from '../components/ButtonCustom.js';
import { auth, firestore } from '../firebase.js';
import { FirebaseRecaptchaVerifierModal, FirebaseRecaptchaBanner } from 'expo-firebase-recaptcha';
import { initializeApp, getApp } from 'firebase/app';
import { getAuth, PhoneAuthProvider, signInWithCredential } from 'firebase/auth';
import { async } from '@firebase/util';
import { collection, query, where, getDocs, doc, runTransaction } from "firebase/firestore";

const app = getApp();

const HomeScreen = ({navigation}) => {
  const [phoneNumber, addPhoneNumber] = React.useState('')
  return(
    <View style={styles.homeScreen}>
      <View style={styles.loginView}>
        <Image source={require('../assets/img/Logo.png')} style={{width:200, height:200, marginTop:20, borderRadius:40}} />
        <Text style={styles.loginText}>WELCOMEüëãüèΩ</Text>
        <Text style={styles.textInfo}>Enter Your Phone Number:</Text>
        <TextInput style={styles.textInput} placeholder={'+11234567899'}
          onChangeText={(text) => addPhoneNumber(text.replace(/ /g, ''))}/>
        <Pressable style={styles.btnOTP}>
          <Text style={{color:Colors.Secondary, fontWeight:'700', fontSize:20, padding:15, borderRadius:30}}>Get OTP</Text>
        </Pressable>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
    homeScreen:{
        backgroundColor: Colors.Primary,
        flex:1,
        alignItems:'center',
    },
    loginView:{
      backgroundColor: Colors.Secondary,
      height: '60%',
      width:'40%',
      borderRadius: 40,
      alignItems:'center',
      marginTop:'5%'
    },
    loginText:{
      color: Colors.Primary,
      fontSize: 40,
      fontWeight: 'bold',
      marginTop: 20,
    },
    textInput:{
      marginTop: 5,
      marginBottom:5,
      borderWidth:2,
      borderRadius:10,
      borderColor: Colors.Primary,
      fontSize: 20,
      height: 40,
      width: 300,
      textAlign: 'center'
    },
    textInfo:{
      color: Colors.Primary,
      fontSize: 20,
      marginTop: 20,
    },
    btnOTP:{
      backgroundColor: Colors.Primary,
    }
});

export default HomeScreen;
