import {View, KeyboardAvoidingView, Text, Platform, TextInput, Modal, Pressable, StyleSheet, Image, Button} from 'react-native';
import React, {useState} from 'react';
import Colors from '../utils/Colors.js';
import { auth, firestore, app } from '../firebase.js';
import { initializeApp, getApp } from 'firebase/app';
import { signInWithPhoneNumber } from 'firebase/auth';
import { getAuth, PhoneAuthProvider, signInWithCredential } from 'firebase/auth';
import { async } from '@firebase/util';
import { collection, query, where, getDocs, doc, runTransaction } from "firebase/firestore";

const HomeScreen = ({navigation}) => {
  const [phoneNumber, addPhoneNumber] = React.useState('');
  const [OTP, setOTP] = React.useState('');
  const [confirm, setConfirm] = useState(null);

  const [code, setCode] = useState('');

  // Handle the button press
  async function signInWithPhoneNumber(phoneNumber) {
    const confirmation = await signInWithPhoneNumber(phoneNumber);
    setConfirm(confirmation);
  }

  async function confirmCode() {
    try {
      await confirm.confirm(code);
    } catch (error) {
      console.log('Invalid code.');
    }
  }

  return(
    <View style={styles.homeScreen}>
      <View style={styles.loginView}>
        <Image source={require('../assets/img/Logo.png')} style={{width:200, height:200, marginTop:20, borderRadius:40}} />

        <Text style={styles.loginText}>WELCOME👋🏽</Text>
        <Text style={styles.textInfo}>Enter Your Phone Number:</Text>
        <TextInput style={styles.textInput} placeholder={'+11234567899'}
          onChangeText={(text) => addPhoneNumber(text.replace(/ /g, ''))}/>
        <Text style={{color:'grey', fontSize:15}} >*You will receive a code on your phone number</Text>
        <Pressable style={styles.btnOTP}>
          <Text style={{color:Colors.Secondary, fontWeight:'700', fontSize:20, padding:10}} onPress={() => signInWithPhoneNumber(phoneNumber)}>Get OTP</Text>
        </Pressable>

        <TextInput style={styles.textInput} placeholder={'Enter your One-Time-Password'}
          onChangeText={(text) => setOTP(text)}/>
        <Pressable style={styles.btnLogin} onPress={() => navigation.replace('Main', {
            paramKey: phoneNumber,
          })}>
          <Text style={{color:Colors.Secondary, fontWeight:'700', fontSize:20, padding:10}}>LOGIN</Text>
        </Pressable>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
    homeScreen:{
        backgroundColor: Colors.Primary,
        flex:1,
        alignItems:'center',
    },
    loginView:{
      backgroundColor: Colors.Secondary,
      height: '80%',
      width:'35%',
      borderRadius: 40,
      alignItems:'center',
      marginTop:'3%'
    },
    loginText:{
      color: Colors.Primary,
      fontSize: 40,
      fontWeight: 'bold',
      marginTop: 20,
    },
    textInput:{
      marginTop: 5,
      marginBottom:5,
      borderWidth:2,
      borderRadius:10,
      borderColor: Colors.Primary,
      fontSize: 20,
      height: 40,
      width: 300,
      textAlign: 'center',
    },
    textInfo:{
      color: Colors.Primary,
      fontSize: 20,
      marginTop: 18,
      fontWeight:'bold',
    },
    btnOTP:{
      backgroundColor: Colors.Primary,
      borderRadius:10,
      marginTop: 10,
      marginBottom: 5
    },
    btnLogin:{
      backgroundColor: Colors.Primary,
      borderRadius:10,
      marginTop: 5
    },
});

export default HomeScreen;
