import {View, KeyboardAvoidingView, Text, Platform, TextInput, Modal, TouchableOpacity, StyleSheet, Image, Button} from 'react-native';
import React, {useState} from 'react';
import Colors from '../utils/Colors.js';
import ButtonCustom from '../components/ButtonCustom.js';
import { auth, firestore } from '../firebase.js';
import { FirebaseRecaptchaVerifierModal, FirebaseRecaptchaBanner } from 'expo-firebase-recaptcha';
import { initializeApp, getApp } from 'firebase/app';
import { getAuth, PhoneAuthProvider, signInWithCredential } from 'firebase/auth';
import { async } from '@firebase/util';
import { collection, query, where, getDocs, doc, runTransaction } from "firebase/firestore";

const app = getApp();

const HomeScreen = ({navigation}) => {
  return(
    <View style={styles.homeScreen}>
      <View style={styles.loginView}>
        <Image source={require('../assets/img/Logo.png')} style={{width:200, height:200, marginTop:25, borderRadius:40}} />
        <Text>HOME</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
    homeScreen:{
        backgroundColor: Colors.WHITE_GREY,
        flex:1,
        alignItems:'center',
    },
    loginView:{
      backgroundColor: Colors.Secondary,
      height: '60%',
      width:'50%',
      borderRadius: 40,
      alignItems:'center',
      marginTop:20
    },
});

export default HomeScreen;
